    base_tile: {
      width: '100%',
      backgroundColor: theme.orbit.paletteWhite,
      // TODO Create token backgroundColorTile
      borderRadius: parseFloat(theme.orbit.borderRadiusNormal),
      borderWidth: 1,
      borderColor: theme.orbit.paletteCloudNormal,
      // TODO Create token borderWidthTile, borderColorTile
      shadowColor: "".concat(theme.orbit.paletteInkNormal, "10"),
      shadowOffset: {
        width: 4,
        height: 2
      },
      shadowOpacity: 0.8,
      shadowRadius: 20,
      elevation: 10
    },
    base_expandablewrapper: {
      paddingVertical: parseFloat(theme.orbit.spaceMedium),
      marginHorizontal: parseFloat(theme.orbit.spaceMedium),
      borderTopWidth: 1,
      borderTopColor: theme.orbit.paletteCloudNormal
    },
    base_description: {
      marginTop: parseFloat(theme.orbit.spaceXXSmall),
      marginEnd: parseFloat(theme.orbit.spaceXLarge)
    },
    withicon_true: {
      marginStart: parseFloat(theme.orbit.spaceXLarge)
    },
    withicon_false: {
      marginStart: 0
    },
    base_tileheader: {
      position: 'relative',
      padding: parseFloat(theme.orbit.spaceMedium) // TODO Create token paddingTile

    },
    base_iconright: {
      position: 'absolute',
      end: 0,
      bottom: 0,
      top: 0,
      justifyContent: 'center',
      paddingEnd: parseFloat(theme.orbit.spaceMedium)
    }
  };
}
