exports.default = generateHeadingStyles;

function generateHeadingStyles(theme) {
  return {
    base_heading: {
      textAlign: 'left'
    },
    headingtype_display: {
      fontFamily: theme.fontFamily[theme.orbit.fontWeightHeadingDisplay],
      fontSize: parseFloat(theme.orbit.fontSizeHeadingDisplay),
      lineHeight: parseFloat(theme.orbit.fontSizeHeadingDisplay) * parseFloat(theme.orbit.lineHeightHeading)
    },
    headingtype_displaysubtitle: {
      fontFamily: theme.fontFamily[theme.orbit.fontWeightNormal],
      fontSize: parseFloat(theme.orbit.fontSizeHeadingTitle2),
      lineHeight: parseFloat(theme.orbit.fontSizeHeadingTitle2) * parseFloat(theme.orbit.lineHeightHeading)
    },
    headingtype_title1: {
      fontFamily: theme.fontFamily[theme.orbit.fontWeightHeadingTitle1],
      fontSize: parseFloat(theme.orbit.fontSizeHeadingTitle1),
      lineHeight: parseFloat(theme.orbit.fontSizeHeadingTitle1) * parseFloat(theme.orbit.lineHeightHeading)
    },
    headingtype_title2: {
      fontFamily: theme.fontFamily[theme.orbit.fontWeightHeadingTitle2],
      fontSize: parseFloat(theme.orbit.fontSizeHeadingTitle2),
      lineHeight: parseFloat(theme.orbit.fontSizeHeadingTitle2) * parseFloat(theme.orbit.lineHeightHeading)
    },
    headingtype_title3: {
      fontFamily: theme.fontFamily[theme.orbit.fontWeightHeadingTitle3],
      fontSize: parseFloat(theme.orbit.fontSizeHeadingTitle3),
      lineHeight: parseFloat(theme.orbit.fontSizeHeadingTitle3) * parseFloat(theme.orbit.lineHeightHeading)
    },
    headingtype_title4: {
      fontFamily: theme.fontFamily[theme.orbit.fontWeightHeadingTitle4],
      fontSize: parseFloat(theme.orbit.fontSizeHeadingTitle4),
      lineHeight: parseFloat(theme.orbit.fontSizeHeadingTitle4) * parseFloat(theme.orbit.lineHeightHeading)
    },
    headingtype_title5: {
      fontFamily: theme.fontFamily[theme.orbit.fontWeightHeadingTitle5],
      fontSize: parseFloat(theme.orbit.fontSizeHeadingTitle5),
      lineHeight: parseFloat(theme.orbit.fontSizeHeadingTitle5) * parseFloat(theme.orbit.lineHeightHeading),
      textTransform: 'uppercase'
    },
    // Heading inverted (aka color)..............................................
    inverted_true: {
      color: theme.orbit.colorHeadingInverted
    },
    inverted_false: {
      color: theme.orbit.colorHeading
    }
  };
}
