"use strict";

var ucCase = require('../../common/utils').ucCase;

module.exports = {
  output: 'src/Icon/icons.names.generated.js',
  input: 'static/icons.json',
  generate: function generateIconTypesAndNames(iconList, HEADER) {
    var icons = {};
    Object.keys(iconList).forEach(function (iconName) {
      icons[iconName] = iconList[iconName].character;
    });
    return "// @flow strict\n\n".concat(HEADER, "\n\nexport const icons = {\n  ").concat(Object.keys(icons).map(function (iconName) {
      return "".concat(ucCase(iconName), ": '").concat(icons[iconName], "',");
    }).join('\n  '), "\n};\n");
  }
};